import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        // Vérifier si nous avons des données en cache
        const cachedData = localStorage.getItem('footballMatches');
        const cachedTimestamp = localStorage.getItem('footballMatchesTimestamp');
        
        // Si nous avons des données en cache de moins d'une heure
        if (cachedData && cachedTimestamp) {
          const now = new Date().getTime();
          if (now - parseInt(cachedTimestamp) < 3600000) { // 1 heure
            setMatches(JSON.parse(cachedData));
            setLoading(false);
            return;
          }
        }

        // Si pas de cache valide, faire la requête API
        const response = await fetch(
          'https://api-football-v1.p.rapidapi.com/v3/fixtures?date=2024-03-17',
          {
            headers: {
              'X-RapidAPI-Key': '9c608c6c02mshc1cce9d78ea4b35p1219d6jsn3cbf6d4e8361',
              'X-RapidAPI-Host': 'api-football-v1.p.rapidapi.com'
            }
          }
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('Données reçues:', data);
        
        if (data.response && data.response.length > 0) {
          // Sauvegarder dans le cache
          localStorage.setItem('footballMatches', JSON.stringify(data.response));
          localStorage.setItem('footballMatchesTimestamp', new Date().getTime().toString());
          setMatches(data.response);
        } else {
          setMatches([]);
        }
        setLoading(false);
      } catch (err) {
        console.error('Erreur détaillée:', err);
        setError(`Erreur de connexion à l'API: ${err.message}`);
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  if (loading) return <div className="loading">Chargement des matches...</div>;
  if (error) return <div className="error">Erreur: {error}</div>;
  if (!matches || matches.length === 0) return <div className="no-matches">Aucun match disponible pour le moment</div>;

  return (
    <div className="app-container">
      <h1>Bienvenue sur Yalla Foot</h1>
      <div className="matches-list">
        {matches.map(match => (
          <div key={match.fixture.id} className="match-card">
            <div className="competition">{match.league.name}</div>
            <div className="match-info">
              <span className="equipe">{match.teams.home.name}</span>
              <span className="score">
                {match.fixture.status.short === 'FT' 
                  ? `${match.goals.home} - ${match.goals.away}`
                  : match.fixture.status.short === 'LIVE'
                  ? `${match.goals.home} - ${match.goals.away} (En cours)`
                  : 'vs'}
              </span>
              <span className="equipe">{match.teams.away.name}</span>
            </div>
            <div className="match-time">
              {new Date(match.fixture.date).toLocaleString('fr-FR', {
                day: '2-digit',
                month: '2-digit',
                hour: '2-digit',
                minute: '2-digit'
              })}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
