import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        // Obtenir la date d'aujourd'hui à 00:00
        const today = new Date();
        today.setHours(0, 0, 0, 0);  // Définir l'heure à minuit

        // Date de fin à demain minuit
        const tomorrow = new Date();
        tomorrow.setDate(today.getDate() + 1);
        tomorrow.setHours(0, 0, 0, 0);

        // Formater les dates en YYYY-MM-DD
        const dateFrom = today.toISOString().split('T')[0];
        const dateTo = tomorrow.toISOString().split('T')[0];

        const response = await fetch(
          `/v4/matches?dateFrom=${dateFrom}&dateTo=${dateTo}`,
          {
            headers: {
              'X-Auth-Token': '3f9226033c324e538d8d34a36118390b',
              'Content-Type': 'application/json'
            }
          }
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('Données reçues:', data);
        setMatches(data.matches || []);
        setLoading(false);
      } catch (err) {
        console.error('Erreur détaillée:', err);
        setError(`Erreur de connexion à l'API: ${err.message}`);
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  if (loading) return <div className="loading">Chargement des matches...</div>;
  if (error) return <div className="error">Erreur: {error}</div>;
  if (!matches || matches.length === 0) return <div className="no-matches">Aucun match disponible pour le moment</div>;

  return (
    <div className="app-container">
      <h1>Bienvenue sur Yalla Foot</h1>
      <div className="matches-list">
        {matches.map(match => (
          <div key={match.id} className="match-card">
            <div className="competition">{match.competition.name}</div>
            <div className="match-info">
              <span className="equipe">{match.homeTeam.name}</span>
              <span className="score">
                {match.status === 'FINISHED' 
                  ? `${match.score.fullTime.home} - ${match.score.fullTime.away}`
                  : match.status === 'IN_PLAY'
                  ? `${match.score.fullTime.home} - ${match.score.fullTime.away} (En cours)`
                  : 'vs'}
              </span>
              <span className="equipe">{match.awayTeam.name}</span>
            </div>
            <div className="match-time">
              {new Date(match.utcDate).toLocaleString('fr-FR', {
                day: '2-digit',
                month: '2-digit',
                hour: '2-digit',
                minute: '2-digit'
              })}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
