import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate, useParams } from 'react-router-dom';
import './App.css';

// Fonction utilitaire pour convertir l'heure dans différents fuseaux horaires
const getTimeInTimeZone = (date, timezone) => {
  return new Date(date).toLocaleTimeString('fr-FR', {
    timeZone: timezone,
    hour: '2-digit',
    minute: '2-digit'
  });
};

// Composant pour la page de détails du match
function MatchDetails() {
  const { id } = useParams();
  const [match, setMatch] = useState(null);
  const [loading, setLoading] = useState(true);

  // Liste des chaînes par compétition
  const getChannelsByLeague = (leagueName) => {
    const channelsMap = {
      'La Liga': [
        { name: 'beIN Sports Premium 1', country: 'MENA' },
        { name: 'beIN Sports HD 3', country: 'MENA' },
        { name: 'SuperSport LaLiga', country: 'Afrique' },
        { name: 'ESPN Deportes', country: 'USA' },
        { name: 'Movistar LaLiga', country: 'Espagne' },
        { name: 'Eleven Sports 1', country: 'Belgique' },
        { name: 'Canal+ Sport', country: 'France' }
      ],
      'Premier League': [
        { name: 'beIN Sports Premium 1', country: 'MENA' },
        { name: 'Canal+ Sport', country: 'France' },
        { name: 'Sky Sports', country: 'UK' }
      ],
      // Ajoutez d'autres ligues selon vos besoins
    };

    return channelsMap[leagueName] || [];
  };

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const response = await fetch(
          `https://v3.football.api-sports.io/fixtures?id=${id}`,
          {
            headers: {
              'x-rapidapi-host': 'v3.football.api-sports.io',
              'x-rapidapi-key': 'd7e2ba82dd1f70a9849322cee345826f'
            }
          }
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        if (data.response && data.response.length > 0) {
          setMatch(data.response[0]);
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching match details:', error);
        setLoading(false);
      }
    };

    fetchMatchDetails();
  }, [id]);

  if (loading) return <div className="loading">Chargement des détails du match...</div>;
  if (!match) return <div className="error">Match non trouvé</div>;

  const channels = getChannelsByLeague(match.league.name);

  return (
    <div className="match-details-container">
      <div className="teams-header">
        <div className="team-detail">
          <img src={match.teams.home.logo} alt={match.teams.home.name} className="team-logo-large" />
          <h2>{match.teams.home.name}</h2>
        </div>
        <div className="match-status">
          <div className="match-time-big">
            {new Date(match.fixture.date).toLocaleString('fr-FR', {
              day: '2-digit',
              month: '2-digit',
              hour: '2-digit',
              minute: '2-digit'
            })}
          </div>
          <div className="league-name">{match.league.name}</div>
        </div>
        <div className="team-detail">
          <img src={match.teams.away.logo} alt={match.teams.away.name} className="team-logo-large" />
          <h2>{match.teams.away.name}</h2>
        </div>
      </div>

      <div className="match-info-details">
        <h3>Informations du match</h3>
        
        <div className="tv-channels">
          <h4>Chaînes de diffusion :</h4>
          {channels.length > 0 ? (
            <ul>
              {channels.map((channel, index) => (
                <li key={index}>
                  <div className="channel-info">
                    <span className="channel-name">
                      {channel.name}
                      <span className="channel-country"> ({channel.country})</span>
                    </span>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>Aucune information de diffusion disponible pour le moment</p>
          )}
        </div>

        {/* Section des fuseaux horaires */}
        <div className="time-zones">
          <h4>Horaires selon les fuseaux horaires :</h4>
          <div className="time-zones-grid">
            {/* Moyen-Orient et Afrique du Nord */}
            <div className="time-zone-group">
              <h5>Moyen-Orient et Afrique du Nord</h5>
              <ul>
                <li>Égypte : {getTimeInTimeZone(match.fixture.date, 'Africa/Cairo')}</li>
                <li>Arabie Saoudite : {getTimeInTimeZone(match.fixture.date, 'Asia/Riyadh')}</li>
                <li>Émirats : {getTimeInTimeZone(match.fixture.date, 'Asia/Dubai')}</li>
                <li>Qatar : {getTimeInTimeZone(match.fixture.date, 'Asia/Qatar')}</li>
                <li>Koweït : {getTimeInTimeZone(match.fixture.date, 'Asia/Kuwait')}</li>
              </ul>
            </div>

            {/* Maghreb */}
            <div className="time-zone-group">
              <h5>Maghreb</h5>
              <ul>
                <li>Algérie : {getTimeInTimeZone(match.fixture.date, 'Africa/Algiers')}</li>
                <li>Maroc : {getTimeInTimeZone(match.fixture.date, 'Africa/Casablanca')}</li>
                <li>Tunisie : {getTimeInTimeZone(match.fixture.date, 'Africa/Tunis')}</li>
              </ul>
            </div>

            {/* Europe */}
            <div className="time-zone-group">
              <h5>Europe</h5>
              <ul>
                <li>France : {getTimeInTimeZone(match.fixture.date, 'Europe/Paris')}</li>
                <li>Belgique : {getTimeInTimeZone(match.fixture.date, 'Europe/Brussels')}</li>
                <li>Suisse : {getTimeInTimeZone(match.fixture.date, 'Europe/Zurich')}</li>
                <li>Royaume-Uni : {getTimeInTimeZone(match.fixture.date, 'Europe/London')}</li>
              </ul>
            </div>

            {/* Amériques */}
            <div className="time-zone-group">
              <h5>Amériques</h5>
              <ul>
                <li>New York : {getTimeInTimeZone(match.fixture.date, 'America/New_York')}</li>
                <li>Montréal : {getTimeInTimeZone(match.fixture.date, 'America/Montreal')}</li>
                <li>Los Angeles : {getTimeInTimeZone(match.fixture.date, 'America/Los_Angeles')}</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// Composant pour la diffusion en direct
function LiveMatch() {
  return (
    <div className="live-match-container">
      <iframe 
        title="Live Matches"
        src="https://embedme.top/embed/charlie/all-matches-live/1"
        width="100%"
        height="100%"
        frameBorder="0"
        scrolling="no"
        allowFullScreen="yes"
        allow="encrypted-media"
      />
    </div>
  );
}

// Composant séparé pour la liste des matches
function MatchesList({ matches }) {
  const navigate = useNavigate();

  const handleMatchClick = (match) => {
    if (match.fixture.status.short === 'NS') {
      // Match pas encore commencé -> afficher les détails
      navigate(`/match/${match.fixture.id}`);
    } else {
      // Match en cours ou terminé -> afficher la diffusion
      navigate(`/live/${match.fixture.id}`);
    }
  };

  return (
    <div className="matches-list">
      {matches.map(match => (
        <div 
          key={match.fixture.id} 
          className="match-card"
          onClick={() => handleMatchClick(match)}
        >
          <div className="competition">
            <img 
              src={match.league.logo} 
              alt={match.league.name} 
              className="league-logo"
            />
            <span>{match.league.name}</span>
          </div>
          <div className="match-info">
            <div className="team-info">
              <div className="team-container">
                <img src={match.teams.home.logo} alt={match.teams.home.name} className="team-logo" />
                <span className="equipe">{match.teams.home.name}</span>
              </div>
            </div>
            <span className="score">
              {match.fixture.status.short === 'FT' 
                ? `${match.goals.home} - ${match.goals.away}`
                : match.fixture.status.short === 'LIVE'
                ? `${match.goals.home} - ${match.goals.away} (En cours)`
                : 'vs'}
            </span>
            <div className="team-info">
              <div className="team-container">
                <img src={match.teams.away.logo} alt={match.teams.away.name} className="team-logo" />
                <span className="equipe">{match.teams.away.name}</span>
              </div>
            </div>
          </div>
          <div className="match-time">
            {new Date(match.fixture.date).toLocaleString('fr-FR', {
              day: '2-digit',
              month: '2-digit',
              hour: '2-digit',
              minute: '2-digit'
            })}
          </div>
        </div>
      ))}
    </div>
  );
}

function App() {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [showLiveMatches, setShowLiveMatches] = useState(false);

  // Définir l'ordre de priorité des compétitions avec leurs IDs et codes pays
  const leaguePriority = {
    'UEFA Champions League': { id: 2, priority: 1 },
    'Premier League': { id: 39, priority: 2 },
    'La Liga': { id: 140, priority: 3 },
    'Ligue 1': { id: 61, priority: 4 },
    'Serie A': { id: 135, priority: 5 },
    'Bundesliga': { id: 78, priority: 6 },
    'UEFA Europa League': { id: 3, priority: 7 },
    'Saudi Professional League': { id: 307, priority: 8 },
    'Egyptian Premier League': { id: 233, priority: 9 },
    'Botola Pro': { id: 200, priority: 10 },
    'A-League': { id: 974, priority: 11 }
  };

  // Ajouter un useEffect pour mettre à jour l'heure chaque minute
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000); // Mise à jour chaque minute

    return () => clearInterval(timer);
  }, []);

  // Données de test
  const testMatches = [
    {
      fixture: {
        id: 1,
        date: "2024-12-13T20:00:00+00:00",
        status: { short: "NS" }
      },
      league: {
        id: 2,
        name: "UEFA Champions League",
        logo: "https://media.api-sports.io/football/leagues/2.png",
        country: "World"
      },
      teams: {
        home: {
          id: 40,
          name: "Real Madrid",
          logo: "https://media.api-sports.io/football/teams/541.png"
        },
        away: {
          id: 41,
          name: "Bayern Munich",
          logo: "https://media.api-sports.io/football/teams/157.png"
        }
      },
      goals: {
        home: null,
        away: null
      }
    },
    {
      fixture: {
        id: 2,
        date: "2024-12-13T19:45:00+00:00",
        status: { short: "LIVE" }
      },
      league: {
        id: 39,
        name: "Premier League",
        logo: "https://media.api-sports.io/football/leagues/39.png",
        country: "England"
      },
      teams: {
        home: {
          id: 42,
          name: "Arsenal",
          logo: "https://media.api-sports.io/football/teams/42.png"
        },
        away: {
          id: 40,
          name: "Liverpool",
          logo: "https://media.api-sports.io/football/teams/40.png"
        }
      },
      goals: {
        home: 2,
        away: 1
      }
    },
    {
      fixture: {
        id: 3,
        date: "2024-12-13T21:00:00+00:00",
        status: { short: "NS" }
      },
      league: {
        id: 140,
        name: "La Liga",
        logo: "https://media.api-sports.io/football/leagues/140.png",
        country: "Spain"
      },
      teams: {
        home: {
          id: 529,
          name: "Barcelona",
          logo: "https://media.api-sports.io/football/teams/529.png"
        },
        away: {
          id: 530,
          name: "Atletico Madrid",
          logo: "https://media.api-sports.io/football/teams/530.png"
        }
      },
      goals: {
        home: null,
        away: null
      }
    }
  ];

  useEffect(() => {
    // Simuler un chargement
    setTimeout(() => {
      setMatches(testMatches);
      setLoading(false);
    }, 1000);

    // Simuler des mises à jour en direct
    const interval = setInterval(() => {
      setMatches(prevMatches => 
        prevMatches.map(match => {
          if (match.fixture.status.short === "LIVE") {
            return {
              ...match,
              goals: {
                home: Math.floor(Math.random() * 5),
                away: Math.floor(Math.random() * 5)
              }
            };
          }
          return match;
        })
      );
    }, 60000);

    return () => clearInterval(interval);
  }, []);

  // Pour le débogage, affichons l'état des matches même s'il est vide
  console.log('État actuel des matches:', matches);

  // Fonction pour filtrer les matches après 11h00
  const filterMatchesAfter11AM = (matches) => {
    return matches.filter(match => {
      const matchTime = new Date(match.fixture.date);
      const hours = matchTime.getHours();
      return hours >= 11;
    });
  };

  // Fonction pour trier les matches
  const sortMatches = (matches) => {
    const now = new Date().getTime();
    const today = new Date().setHours(0, 0, 0, 0);
    const tomorrow = new Date(today + 24 * 60 * 60 * 1000).setHours(0, 0, 0, 0);
    
    // Filtrer uniquement les matches du jour
    const todayMatches = matches.filter(match => {
      const matchDate = new Date(match.fixture.date).getTime();
      return matchDate >= today && matchDate < tomorrow;
    });
    
    // Séparer les matches en deux groupes : en cours et à venir
    const liveMatches = [];
    const upcomingMatches = [];
    
    todayMatches.forEach(match => {
      if (match.fixture.status.short === 'LIVE' || 
          match.fixture.status.short === '1H' || 
          match.fixture.status.short === '2H' || 
          match.fixture.status.short === 'HT') {
        liveMatches.push(match);
      } else if (match.fixture.status.short === 'NS' && new Date(match.fixture.date).getTime() >= now) {
        upcomingMatches.push(match);
      }
    });

    // Trier les matches à venir par heure
    upcomingMatches.sort((a, b) => {
      const timeA = new Date(a.fixture.date).getTime();
      const timeB = new Date(b.fixture.date).getTime();
      return timeA - timeB;
    });

    // Combiner les groupes : d'abord en cours, puis à venir
    return [...liveMatches, ...upcomingMatches];
  };

  // Fonction pour grouper les matches par compétition
  const groupMatchesByLeague = (matches) => {
    // D'abord, filtrer les matches après 11h
    const filteredMatches = filterMatchesAfter11AM(matches);
    
    // Ne garder que les matches des compétitions prioritaires en vérifiant l'ID
    const priorityMatches = filteredMatches.filter(match => {
      const leagueEntry = Object.entries(leaguePriority).find(([name, data]) => 
        data.id === match.league.id
      );
      return leagueEntry != null;
    });
    
    // Grouper les matches par ligue
    const groups = priorityMatches.reduce((groups, match) => {
      const leagueId = match.league.id;
      const leagueEntry = Object.entries(leaguePriority).find(([name, data]) => 
        data.id === leagueId
      );
      
      if (leagueEntry && !groups[leagueId]) {
        groups[leagueId] = {
          leagueInfo: match.league,
          matches: [],
          priority: leagueEntry[1].priority
        };
      }
      
      if (groups[leagueId]) {
        groups[leagueId].matches.push(match);
      }
      
      return groups;
    }, {});

    // Pour chaque groupe, trier les matches (en cours d'abord, puis par heure)
    Object.values(groups).forEach(group => {
      group.matches.sort((a, b) => {
        const isLiveA = a.fixture.status.short === 'LIVE' || 
                       a.fixture.status.short === '1H' || 
                       a.fixture.status.short === '2H' || 
                       a.fixture.status.short === 'HT';
        const isLiveB = b.fixture.status.short === 'LIVE' || 
                       b.fixture.status.short === '1H' || 
                       b.fixture.status.short === '2H' || 
                       b.fixture.status.short === 'HT';

        if (isLiveA && !isLiveB) return -1;
        if (!isLiveA && isLiveB) return 1;

        return new Date(a.fixture.date) - new Date(b.fixture.date);
      });
    });

    return groups;
  };

  // Trier les groupes de ligues
  const sortGroups = (groups) => {
    return Object.values(groups).sort((a, b) => {
      // D'abord par priorité de la ligue
      if (a.priority !== b.priority) {
        return a.priority - b.priority;
      }

      // Ensuite, vérifier les matches en cours
      const hasLiveA = a.matches.some(m => m.fixture.status.short === 'LIVE' || 
                                          m.fixture.status.short === '1H' || 
                                          m.fixture.status.short === '2H' || 
                                          m.fixture.status.short === 'HT');
      const hasLiveB = b.matches.some(m => m.fixture.status.short === 'LIVE' || 
                                          m.fixture.status.short === '1H' || 
                                          m.fixture.status.short === '2H' || 
                                          m.fixture.status.short === 'HT');
      
      if (hasLiveA && !hasLiveB) return -1;
      if (!hasLiveA && hasLiveB) return 1;
      
      // Si même priorité et pas de match en cours, trier par heure du prochain match
      return new Date(a.matches[0].fixture.date) - new Date(b.matches[0].fixture.date);
    });
  };

  const handleWatchLiveClick = () => {
    setShowLiveMatches(true);
  };

  const handleCloseLive = () => {
    setShowLiveMatches(false);
  };

  if (loading) return <div className="loading">Chargement des matches...</div>;
  if (error) return <div className="error">Erreur: {error}</div>;
  if (!matches || matches.length === 0) {
    return (
      <div className="app-container">
        <div className="header">
          <div className="current-datetime">
            <div className="current-time">
              {currentTime.toLocaleTimeString('fr-FR', {
                hour: '2-digit',
                minute: '2-digit'
              })}
            </div>
            <div className="current-date">
              {currentTime.toLocaleDateString('fr-FR', {
                weekday: 'long',
                day: 'numeric',
                month: 'long',
                year: 'numeric'
              })}
            </div>
          </div>
          <h1>مراد فالسطاد - Matches en Direct</h1>
        </div>
        
        <button className="watch-live-button" onClick={() => setShowLiveMatches(true)}>
          <span className="live-dot"></span>
          Regarder tous les matches en direct
        </button>

        {showLiveMatches && (
          <div className="live-match-container">
            <button className="close-live-button" onClick={() => setShowLiveMatches(false)}>
              ✕
            </button>
            <iframe 
              src="https://embedme.top/embed/charlie/all-matches-live/1"
              width="100%"
              height="100%"
              frameBorder="0"
              allowFullScreen
              allow="encrypted-media"
            ></iframe>
          </div>
        )}

        <div className="no-matches">Aucun match disponible pour le moment</div>
      </div>
    );
  }

  const groupedMatches = groupMatchesByLeague(matches);
  const sortedGroups = sortGroups(groupedMatches);

  return (
    <div className="app-container">
      <div className="header">
        <div className="current-datetime">
          <div className="current-time">
            {currentTime.toLocaleTimeString('fr-FR', {
              hour: '2-digit',
              minute: '2-digit'
            })}
          </div>
          <div className="current-date">
            {currentTime.toLocaleDateString('fr-FR', {
              weekday: 'long',
              day: 'numeric',
              month: 'long',
              year: 'numeric'
            })}
          </div>
        </div>
        <h1>مراد فالسطاد - Matches en Direct</h1>
      </div>
      
      <button className="watch-live-button" onClick={handleWatchLiveClick}>
        <span className="live-dot"></span>
        Regarder tous les matches en direct
      </button>

      {showLiveMatches && (
        <div className="live-match-container">
          <button className="close-live-button" onClick={handleCloseLive}>
            ✕
          </button>
          <iframe 
            src="https://embedme.top/embed/charlie/all-matches-live/1"
            width="100%"
            height="100%"
            frameBorder="0"
            allowFullScreen
            allow="encrypted-media"
          ></iframe>
        </div>
      )}

      <div className="matches-list">
        {sortedGroups.map(({ leagueInfo, matches }) => (
          <div key={leagueInfo.id} className="league-section">
            <div className="competition">
              <img 
                src={leagueInfo.logo} 
                alt={leagueInfo.name} 
                className="league-logo"
              />
              <span>{leagueInfo.name}</span>
              <img 
                src={`https://flagcdn.com/24x18/${leaguePriority[leagueInfo.name]?.countryCode.toLowerCase()}.png`}
                alt={`Drapeau ${leagueInfo.country}`}
                className="country-flag"
              />
            </div>
            <div className="league-matches">
              {matches.map(match => (
                <div key={match.fixture.id} className="match-card">
                  <div className="match-info">
                    <div className="team-info">
                      <div className="team-container">
                        <img 
                          src={match.teams.home.logo} 
                          alt={match.teams.home.name} 
                          className="team-logo"
                        />
                        <span className="equipe">{match.teams.home.name}</span>
                      </div>
                    </div>
                    <span className="score">
                      {match.fixture.status.short === 'FT' 
                        ? `${match.goals.home} - ${match.goals.away}`
                        : match.fixture.status.short === 'LIVE'
                        ? `${match.goals.home} - ${match.goals.away} (En cours)`
                        : 'vs'}
                    </span>
                    <div className="team-info">
                      <div className="team-container">
                        <img 
                          src={match.teams.away.logo} 
                          alt={match.teams.away.name} 
                          className="team-logo"
                        />
                        <span className="equipe">{match.teams.away.name}</span>
                      </div>
                    </div>
                  </div>
                  <div className="match-time">
                    {new Date(match.fixture.date).toLocaleString('fr-FR', {
                      day: '2-digit',
                      month: '2-digit',
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
