import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate, useParams } from 'react-router-dom';
import './App.css';

// Fonction utilitaire pour convertir l'heure dans différents fuseaux horaires
const getTimeInTimeZone = (date, timezone) => {
  return new Date(date).toLocaleTimeString('fr-FR', {
    timeZone: timezone,
    hour: '2-digit',
    minute: '2-digit'
  });
};

// Composant pour la page de détails du match
function MatchDetails() {
  const { id } = useParams();
  const [match, setMatch] = useState(null);
  const [loading, setLoading] = useState(true);

  // Liste des chaînes par compétition
  const getChannelsByLeague = (leagueName) => {
    const channelsMap = {
      'La Liga': [
        { name: 'beIN Sports Premium 1', country: 'MENA' },
        { name: 'beIN Sports HD 3', country: 'MENA' },
        { name: 'SuperSport LaLiga', country: 'Afrique' },
        { name: 'ESPN Deportes', country: 'USA' },
        { name: 'Movistar LaLiga', country: 'Espagne' },
        { name: 'Eleven Sports 1', country: 'Belgique' },
        { name: 'Canal+ Sport', country: 'France' }
      ],
      'Premier League': [
        { name: 'beIN Sports Premium 1', country: 'MENA' },
        { name: 'Canal+ Sport', country: 'France' },
        { name: 'Sky Sports', country: 'UK' }
      ],
      // Ajoutez d'autres ligues selon vos besoins
    };

    return channelsMap[leagueName] || [];
  };

  useEffect(() => {
    const fetchMatchDetails = async () => {
      try {
        const response = await fetch(
          `https://v3.football.api-sports.io/fixtures?id=${id}`,
          {
            headers: {
              'x-rapidapi-host': 'v3.football.api-sports.io',
              'x-rapidapi-key': 'd7e2ba82dd1f70a9849322cee345826f'
            }
          }
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        if (data.response && data.response.length > 0) {
          setMatch(data.response[0]);
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching match details:', error);
        setLoading(false);
      }
    };

    fetchMatchDetails();
  }, [id]);

  if (loading) return <div className="loading">Chargement des détails du match...</div>;
  if (!match) return <div className="error">Match non trouvé</div>;

  const channels = getChannelsByLeague(match.league.name);

  return (
    <div className="match-details-container">
      <div className="teams-header">
        <div className="team-detail">
          <img src={match.teams.home.logo} alt={match.teams.home.name} className="team-logo-large" />
          <h2>{match.teams.home.name}</h2>
        </div>
        <div className="match-status">
          <div className="match-time-big">
            {new Date(match.fixture.date).toLocaleString('fr-FR', {
              day: '2-digit',
              month: '2-digit',
              hour: '2-digit',
              minute: '2-digit'
            })}
          </div>
          <div className="league-name">{match.league.name}</div>
        </div>
        <div className="team-detail">
          <img src={match.teams.away.logo} alt={match.teams.away.name} className="team-logo-large" />
          <h2>{match.teams.away.name}</h2>
        </div>
      </div>

      <div className="match-info-details">
        <h3>Informations du match</h3>
        
        <div className="tv-channels">
          <h4>Chaînes de diffusion :</h4>
          {channels.length > 0 ? (
            <ul>
              {channels.map((channel, index) => (
                <li key={index}>
                  <div className="channel-info">
                    <span className="channel-name">
                      {channel.name}
                      <span className="channel-country"> ({channel.country})</span>
                    </span>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>Aucune information de diffusion disponible pour le moment</p>
          )}
        </div>

        {/* Section des fuseaux horaires */}
        <div className="time-zones">
          <h4>Horaires selon les fuseaux horaires :</h4>
          <div className="time-zones-grid">
            {/* Moyen-Orient et Afrique du Nord */}
            <div className="time-zone-group">
              <h5>Moyen-Orient et Afrique du Nord</h5>
              <ul>
                <li>Égypte : {getTimeInTimeZone(match.fixture.date, 'Africa/Cairo')}</li>
                <li>Arabie Saoudite : {getTimeInTimeZone(match.fixture.date, 'Asia/Riyadh')}</li>
                <li>Émirats : {getTimeInTimeZone(match.fixture.date, 'Asia/Dubai')}</li>
                <li>Qatar : {getTimeInTimeZone(match.fixture.date, 'Asia/Qatar')}</li>
                <li>Koweït : {getTimeInTimeZone(match.fixture.date, 'Asia/Kuwait')}</li>
              </ul>
            </div>

            {/* Maghreb */}
            <div className="time-zone-group">
              <h5>Maghreb</h5>
              <ul>
                <li>Algérie : {getTimeInTimeZone(match.fixture.date, 'Africa/Algiers')}</li>
                <li>Maroc : {getTimeInTimeZone(match.fixture.date, 'Africa/Casablanca')}</li>
                <li>Tunisie : {getTimeInTimeZone(match.fixture.date, 'Africa/Tunis')}</li>
              </ul>
            </div>

            {/* Europe */}
            <div className="time-zone-group">
              <h5>Europe</h5>
              <ul>
                <li>France : {getTimeInTimeZone(match.fixture.date, 'Europe/Paris')}</li>
                <li>Belgique : {getTimeInTimeZone(match.fixture.date, 'Europe/Brussels')}</li>
                <li>Suisse : {getTimeInTimeZone(match.fixture.date, 'Europe/Zurich')}</li>
                <li>Royaume-Uni : {getTimeInTimeZone(match.fixture.date, 'Europe/London')}</li>
              </ul>
            </div>

            {/* Amériques */}
            <div className="time-zone-group">
              <h5>Amériques</h5>
              <ul>
                <li>New York : {getTimeInTimeZone(match.fixture.date, 'America/New_York')}</li>
                <li>Montréal : {getTimeInTimeZone(match.fixture.date, 'America/Montreal')}</li>
                <li>Los Angeles : {getTimeInTimeZone(match.fixture.date, 'America/Los_Angeles')}</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// Composant pour la diffusion en direct
function LiveMatch({ match }) {
  const embedUrl = `https://embedme.top/embed/charlie/${match.teams.home.name.toLowerCase()}-vs-${match.teams.away.name.toLowerCase()}-${match.fixture.id}/1`;
  
  return (
    <div className="live-match-container">
      <iframe 
        title={`${match.teams.home.name} vs ${match.teams.away.name}`}
        marginHeight="0"
        marginWidth="0"
        src={embedUrl}
        scrolling="no"
        allowFullScreen="yes"
        allow="encrypted-media; picture-in-picture;"
        width="100%"
        height="100%"
        frameBorder="0"
      />
    </div>
  );
}

// Composant séparé pour la liste des matches
function MatchesList({ matches }) {
  const navigate = useNavigate();

  const handleMatchClick = (match) => {
    if (match.fixture.status.short === 'NS') {
      // Match pas encore commencé -> afficher les détails
      navigate(`/match/${match.fixture.id}`);
    } else {
      // Match en cours ou terminé -> afficher la diffusion
      navigate(`/live/${match.fixture.id}`);
    }
  };

  return (
    <div className="matches-list">
      {matches.map(match => (
        <div 
          key={match.fixture.id} 
          className="match-card"
          onClick={() => handleMatchClick(match)}
        >
          <div className="competition">
            <img 
              src={match.league.logo} 
              alt={match.league.name} 
              className="league-logo"
            />
            <span>{match.league.name}</span>
          </div>
          <div className="match-info">
            <div className="team-info">
              <div className="team-container">
                <img src={match.teams.home.logo} alt={match.teams.home.name} className="team-logo" />
                <span className="equipe">{match.teams.home.name}</span>
              </div>
            </div>
            <span className="score">
              {match.fixture.status.short === 'FT' 
                ? `${match.goals.home} - ${match.goals.away}`
                : match.fixture.status.short === 'LIVE'
                ? `${match.goals.home} - ${match.goals.away} (En cours)`
                : 'vs'}
            </span>
            <div className="team-info">
              <div className="team-container">
                <img src={match.teams.away.logo} alt={match.teams.away.name} className="team-logo" />
                <span className="equipe">{match.teams.away.name}</span>
              </div>
            </div>
          </div>
          <div className="match-time">
            {new Date(match.fixture.date).toLocaleString('fr-FR', {
              day: '2-digit',
              month: '2-digit',
              hour: '2-digit',
              minute: '2-digit'
            })}
          </div>
        </div>
      ))}
    </div>
  );
}

function App() {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const today = new Date();
        const dateStr = today.toISOString().split('T')[0];

        const response = await fetch(
          `https://v3.football.api-sports.io/fixtures?date=${dateStr}`,
          {
            method: 'GET',
            headers: {
              'x-rapidapi-host': 'v3.football.api-sports.io',
              'x-rapidapi-key': 'd7e2ba82dd1f70a9849322cee345826f'
            }
          }
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('Données reçues:', data);
        
        if (data.response && data.response.length > 0) {
          setMatches(data.response);
        } else {
          setMatches([]);
        }
        setLoading(false);
      } catch (err) {
        console.error('Erreur détaillée:', err);
        setError(`Erreur de connexion à l'API: ${err.message}`);
        setLoading(false);
      }
    };

    fetchMatches();
  }, []);

  // Fonction pour filtrer les matches après 11h00
  const filterMatchesAfter11AM = (matches) => {
    return matches.filter(match => {
      const matchTime = new Date(match.fixture.date);
      const hours = matchTime.getHours();
      return hours >= 11;
    });
  };

  // Fonction pour trier les matches
  const sortMatches = (matches) => {
    const now = new Date().getTime();
    
    // Séparer les matches en trois groupes : en cours, à venir et terminés
    const liveMatches = [];
    const upcomingMatches = [];
    const finishedMatches = [];
    
    matches.forEach(match => {
      if (match.fixture.status.short === 'LIVE' || match.fixture.status.short === '1H' || 
          match.fixture.status.short === '2H' || match.fixture.status.short === 'HT') {
        liveMatches.push(match);
      } else if (match.fixture.status.short === 'NS' && new Date(match.fixture.date).getTime() >= now) {
        upcomingMatches.push(match);
      } else {
        finishedMatches.push(match);
      }
    });

    // Trier les matches à venir par heure
    upcomingMatches.sort((a, b) => {
      const timeA = new Date(a.fixture.date).getTime();
      const timeB = new Date(b.fixture.date).getTime();
      return timeA - timeB;
    });

    // Trier les matches terminés (plus récent en premier)
    finishedMatches.sort((a, b) => {
      const timeA = new Date(a.fixture.date).getTime();
      const timeB = new Date(b.fixture.date).getTime();
      return timeB - timeA;
    });

    // Combiner les trois groupes : d'abord les matches en cours, puis à venir, puis terminés
    return [...liveMatches, ...upcomingMatches, ...finishedMatches];
  };

  // Fonction pour grouper les matches par compétition
  const groupMatchesByLeague = (matches) => {
    // D'abord, filtrer les matches après 11h
    const filteredMatches = filterMatchesAfter11AM(matches);
    // Ensuite, trier les matches selon la nouvelle logique
    const sortedMatches = sortMatches(filteredMatches);
    
    // Grouper les matches triés par ligue
    return sortedMatches.reduce((groups, match) => {
      const leagueId = match.league.id;
      if (!groups[leagueId]) {
        groups[leagueId] = {
          leagueInfo: match.league,
          matches: []
        };
      }
      groups[leagueId].matches.push(match);
      return groups;
    }, {});
  };

  // Trier les groupes de ligues en priorisant ceux avec des matches en cours
  const sortGroups = (groups) => {
    return Object.values(groups).sort((a, b) => {
      const hasLiveA = a.matches.some(m => m.fixture.status.short === 'LIVE');
      const hasLiveB = b.matches.some(m => m.fixture.status.short === 'LIVE');
      
      if (hasLiveA && !hasLiveB) return -1;
      if (!hasLiveA && hasLiveB) return 1;
      
      // Si aucun match en direct, trier par le prochain match
      const nextMatchA = a.matches[0];
      const nextMatchB = b.matches[0];
      return new Date(nextMatchA.fixture.date) - new Date(nextMatchB.fixture.date);
    });
  };

  if (loading) return <div className="loading">Chargement des matches...</div>;
  if (error) return <div className="error">Erreur: {error}</div>;
  if (!matches || matches.length === 0) return <div className="no-matches">Aucun match disponible pour le moment</div>;

  const groupedMatches = groupMatchesByLeague(matches);
  const sortedGroups = sortGroups(groupedMatches);

  return (
    <div className="app-container">
      <h1>مراد فالسطاد - Matches en Direct</h1>
      <div className="matches-list">
        {sortedGroups.map(({ leagueInfo, matches }) => (
          <div key={leagueInfo.id} className="league-section">
            <div className="competition">
              <img 
                src={leagueInfo.logo} 
                alt={leagueInfo.name} 
                className="league-logo"
              />
              <span>{leagueInfo.name}</span>
            </div>
            <div className="league-matches">
              {matches.map(match => (
                <div key={match.fixture.id} className="match-card">
                  <div className="match-info">
                    <div className="team-info">
                      <div className="team-container">
                        <img 
                          src={match.teams.home.logo} 
                          alt={match.teams.home.name} 
                          className="team-logo"
                        />
                        <span className="equipe">{match.teams.home.name}</span>
                      </div>
                    </div>
                    <span className="score">
                      {match.fixture.status.short === 'FT' 
                        ? `${match.goals.home} - ${match.goals.away}`
                        : match.fixture.status.short === 'LIVE'
                        ? `${match.goals.home} - ${match.goals.away} (En cours)`
                        : 'vs'}
                    </span>
                    <div className="team-info">
                      <div className="team-container">
                        <img 
                          src={match.teams.away.logo} 
                          alt={match.teams.away.name} 
                          className="team-logo"
                        />
                        <span className="equipe">{match.teams.away.name}</span>
                      </div>
                    </div>
                  </div>
                  <div className="match-time">
                    {new Date(match.fixture.date).toLocaleString('fr-FR', {
                      day: '2-digit',
                      month: '2-digit',
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
